package com.hoioy.diamond.sys.api;import com.hoioy.diamond.common.api.BaseController;import com.hoioy.diamond.common.dto.ResultDTO;import com.hoioy.diamond.sys.dto.FileStorageDTO;import com.hoioy.diamond.sys.service.IFileStorageService;import com.github.xiaoymin.knife4j.annotations.ApiOperationSupport;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.multipart.MultipartFile;import java.io.IOException;@Api(tags = {"09.文件上传接口"})@RestController@RequestMapping("/filesRest")public class FileStorageController extends BaseController<IFileStorageService, FileStorageDTO> {    @PostMapping("/")    @ApiOperation(value = "上传文件")    public ResultDTO<String> handleFileUpload(@RequestParam("file") MultipartFile file) throws IOException {        String id = iBaseService.saveFile(file, "FileStorageController/", file.getName());        return new ResultDTO<String>(id);    }}