package com.hoioy.diamond.sys.api;import com.hoioy.diamond.common.api.BaseController;import com.hoioy.diamond.common.dto.PageDTO;import com.hoioy.diamond.common.dto.ResultDTO;import com.hoioy.diamond.sys.dto.DataItemDTO;import com.hoioy.diamond.sys.service.IDataItemService;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@Api(tags = {"11.数据字典操作接口"})@RequestMapping("/sys/dataitem")public class DataItemController extends BaseController<IDataItemService, DataItemDTO> {    /**     * 获取所有菜单信息     * TODO zhaozhao 不建议提供全量查询接口，建议提供根据parentId动态构建树的方式     */    @ApiOperation(value = "加载全部数据字典树")    @GetMapping(value = "/query/all")    public ResultDTO queryAllTree(String parentId) {        PageDTO pageDTO = new PageDTO();        pageDTO.setPageSize(Integer.MAX_VALUE);        pageDTO.setPage(1);        pageDTO = iBaseService.getPage(pageDTO);        List tree = iBaseService.listToTree(pageDTO.getList(), parentId);        pageDTO.setList(tree);        return new ResultDTO(pageDTO);    }}