package com.hoioy.diamond.sys.api;import com.hoioy.diamond.common.api.BaseController;import com.hoioy.diamond.common.dto.PageDTO;import com.hoioy.diamond.common.dto.ResultDTO;import com.hoioy.diamond.sys.dto.DataItemTypeDTO;import com.hoioy.diamond.sys.service.IDataItemTypeService;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@Api(tags = {"11.数据字典类型操作接口"})@RequestMapping("sys/data-item-type")public class DataItemTypeController extends BaseController<IDataItemTypeService, DataItemTypeDTO> {    @ApiOperation(value = "查询所有字典类型")    @GetMapping("/search/all")    public ResultDTO roleList() {        PageDTO pageDTO = new PageDTO();        pageDTO.setPage(1);        pageDTO.setPageSize(Integer.MAX_VALUE);        pageDTO = iBaseService.getPage(pageDTO);        return new ResultDTO(pageDTO.getList());    }}