package com.hoioy.diamond.sys.api;import com.hoioy.diamond.common.api.BaseController;import com.hoioy.diamond.common.dto.PageDTO;import com.hoioy.diamond.common.dto.ResultDTO;import com.hoioy.diamond.sys.dto.DataItemTypeDTO;import com.hoioy.diamond.sys.service.IDataItemTypeService;import com.github.xiaoymin.knife4j.annotations.ApiOperationSupport;import com.github.xiaoymin.knife4j.annotations.ApiSupport;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@Api(tags = {"12.数据字典类型操作接口"})@ApiSupport(order = 12)@RequestMapping("sys/data-item-type")public class DataItemTypeController extends BaseController<IDataItemTypeService, DataItemTypeDTO> {    @ApiOperation(value = "8.查询所有字典类型")    @ApiOperationSupport(order = 8)    @GetMapping("/search/all")    public ResultDTO roleList() {        PageDTO pageDTO = new PageDTO();        pageDTO.setPage(1);        pageDTO.setPageSize(100);        pageDTO = iBaseService.getPage(pageDTO);        return new ResultDTO(pageDTO.getList());    }}