package com.hoioy.diamond.sys.api;import com.hoioy.diamond.common.api.BaseController;import com.hoioy.diamond.common.dto.ResultDTO;import com.hoioy.diamond.sys.dto.FileStorageDTO;import com.hoioy.diamond.sys.service.IFileStorageService;import com.hoioy.diamond.sys.web.BaseWebMvcConfigurer;import com.github.xiaoymin.knife4j.annotations.ApiOperationSupport;import com.github.xiaoymin.knife4j.annotations.ApiSupport;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.multipart.MultipartFile;import java.io.File;import java.io.IOException;@Api(tags = {"13.文件上传接口"})@ApiSupport(order = 13)@RestController@RequestMapping("/file")public class FileStorageController extends BaseController<IFileStorageService, FileStorageDTO> {    @PostMapping("/upload")    @ApiOperation(value = "8.上传文件")    @ApiOperationSupport(order = 8)    public ResultDTO<String> handleFileUpload(@RequestParam("file") MultipartFile file) throws IOException {        String id = iBaseService.saveFile(file, BaseWebMvcConfigurer.secondLevelDirectory + File.separator, file.getOriginalFilename()).getId();        return new ResultDTO<String>(id);    }}