mapper.select:
select

from
   jiayin_msg_draft draft
left join jiayin_msg_type mst on draft.msg_type_id=mst.id
where
draft.flag=1

AND draft.msg_title = #{msgDraft.msgTitle}


    AND draft.openid = #{msgDraft.openid}


    AND mst.msg_type_id = #{msgDraft.msgTypeId}


    AND draft.msg_id = #{msgDraft.msgId}


    AND draft.message_type = #{msgDraft.messageType}
